CREATE TABLE IF NOT EXISTS users ( id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, name varchar(100), email varchar(200) );
CREATE TABLE IF NOT EXISTS items ( id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, name varchar(100), description varchar(500), is_available boolean, owner_id BIGINT, request_id BIGINT, CONSTRAINT fk_items_to_users FOREIGN KEY(owner_id) REFERENCES users(id) );
CREATE TABLE IF NOT EXISTS bookings (id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, start_moment timestamp, end_moment timestamp, item_id BIGINT, booker_id BIGINT, status varchar(50), CONSTRAINT fk_bookings_to_items FOREIGN KEY(item_id) REFERENCES items(id), CONSTRAINT fk_bookings_to_users FOREIGN KEY(booker_id) REFERENCES users(id) );
CREATE TABLE IF NOT EXISTS comments (id BIGINT GENERATED ALWAYS AS IDENTITY PRIMARY KEY, text varchar(1000), item_id BIGINT, author_id BIGINT, created timestamp, CONSTRAINT fk_comments_to_items FOREIGN KEY(item_id) REFERENCES items(id), CONSTRAINT fk_comments_to_users FOREIGN KEY(author_id) REFERENCES users(id) );
